body.script:
window.addEventListener('load', () => {
  const svg = document.getElementById('autoFitSVG');
  const foreign = document.getElementById('foreign');

  // Use a timeout to allow animation DOM to settle
  setTimeout(() => {
    const bbox = svg.getBBox();
    svg.setAttribute('width', bbox.width);
    svg.setAttribute('height', bbox.height);
    foreign.setAttribute('width', bbox.width);
    foreign.setAttribute('height', bbox.height);
  }, 100); // small delay to let text render
});
